import React from "react";
import defaultBvg from '../images/room-1.jpeg'
import Hero from "../components/Hero";
import Banner from "../components/Banner";
import { Link } from 'react-router-dom'
import { RoomContext } from "../context";
import StyledHero from "../components/StyledHero";


export  default class SingleRoom extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            slug: this.props.match.params.slug,
            defaultBvg
        }
    }
    static contextType = RoomContext

    componentDidMount() {

    }

    render() {
        const {getRoom} = this.context
        const room = getRoom(this.state.slug)
        if(!room) {
            return (
                <div className="error">
                    <h3>No such room could be found</h3>
                    <Link to="/rooms" className="btn-primary">Back to Rooms</Link>
                </div>
            )
        }
        const {name, description, capacity, siz, price, extras, breakfast, pets, images } = room
        const [mainImg, ...defaultImg] = images
        return (
            <>
            <StyledHero img={images[0] || this.state.defaultBvg}>
                <Banner title={`${name} room`}>
                    <Link to="/rooms" className='btn-primary'>
                        back to rooms
                    </Link>
                </Banner>
            </StyledHero>
                <section className="single-room">
                    <div className="single-room-images">
                        {defaultImg.map((item, index) => {
                            return <img key={index} src={item} alt={name} />
                        })}
                    </div>
                    <div className="single-room-info">
                        <article className="desc">
                            <h3>Details</h3>
                            <p>{description}</p>
                        </article>
                    </div>
                </section>
                </>
        )
    }

}
